import { createAsyncThunk } from "@reduxjs/toolkit";
import { NewPlayerDto } from "../../api/dto/player.g";

export const fetchAddPlayer = createAsyncThunk<any, NewPlayerDto>(
  "player/addPlayer",
  async (data) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: JSON.stringify(data),
    };
    const response = await fetch(
      "http://dev.trainee.dex-it.ru/api/Player/Add",
      requestOptions
    );
    return await response.json();
  }
);

export const fetchPlayerPositionsAsync = createAsyncThunk<any, string>(
  "player/fetchPlayerPosition",
  async (request) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "GET",
      headers: myHeaders,
    };
    const response = await fetch(request, requestOptions);
    return (await response.json()) as string[];
  }
);
