import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit'
import { RootState } from "./store"

interface IGetTeams {
  isLoading: boolean,
  data: [
    {
      name: string,
      foundationYear: number,
      division: string,
      conference: string,
      imageUrl: string,
      id: number
    }?
  ],
  count: number,
  page: number,
  size: number
  }

const initialState: IGetTeams = {
  isLoading: false,
  data: [],
  count: 6,
  page: 1,
  size: 6
}

export const getTeamsSlice = createSlice({
    name: 'getTeamsSlice',
    initialState,
    reducers: {
      getTeams: (state, action)
    }
})

export const selectTeamsData = (state: RootState) => state.getTeams.data;
export const selectTeamsIsLoading = (state: RootState) => state.getTeams.isLoading;

export default getTeamsSlice.reducer;
