import React, { useEffect } from "react";
import { useForm } from "react-hook-form";
import {
  fetchPlayerPositions,
  SelectPlayerPositionsData,
  SelectPlayerPositionsIsLoading,
} from "../../store/getPlayerPositionsSlice";
import {
  fetchTeamsAsync,
  selectTeamsData,
  selectTeamsIsLoading,
} from "../../store/getTeamsSlice";
import { useAppDispatch, useAppSelector } from "../../store/hooks";

type Inputs = {
  playerName: string;
  playerPosition: string;
  playerTeam: string;
  playerHeight: string;
  playerWeight: string;
  playerBirthday: string;
  playerNumber: string;
};

const AddPlayer: React.FC = () => {
  const dispatch = useAppDispatch();

  useEffect(() => {
    dispatch(
      fetchPlayerPositions(
        "http://dev.trainee.dex-it.ru/api/Player/GetPositions"
      )
    );
    dispatch(fetchTeamsAsync("http://dev.trainee.dex-it.ru/api/Team/GetTeams"));
  }, [dispatch]);

  const playerPosition = useAppSelector(SelectPlayerPositionsData);
  const playerPositionIsLoading = useAppSelector(
    SelectPlayerPositionsIsLoading
  );
  const teamsRedux = useAppSelector(selectTeamsData);
  const teamsIsLoading = useAppSelector(selectTeamsIsLoading);
  const teams = teamsRedux.data;

  const { register, handleSubmit, errors } = useForm<Inputs>();
  const onSubmit = (data: {}) => console.log(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="form-row">
        <label htmlFor="playerName">Name</label>
        <input
          type="text"
          name="playerName"
          id="playerName"
          ref={register({ required: true })}
        />
        {errors.playerName && (
          <div className="error">This field is required</div>
        )}
      </div>
      <div className="form-row">
        <label htmlFor="playerPosition">Position</label>
        <select name="playerPosition" ref={register({ required: true })}>
          <option value="" disabled>
            Select...
          </option>
          {playerPositionIsLoading
            ? "Loading"
            : playerPosition.map((item) => (
                <option value={item}>{item}</option>
              ))}
        </select>
        {errors.playerPosition && (
          <div className="error">This field is required</div>
        )}
      </div>
      <div className="form-row">
        <label htmlFor="playerTeam">Position</label>
        <select name="playerTeam" ref={register({ required: true })}>
          <option value="" disabled>
            Select...
          </option>
          {teamsIsLoading
            ? "Loading"
            : teams.map((item) => (
                <option value={item.name}>{item.name}</option>
              ))}
        </select>
        {errors.playerTeam && (
          <div className="error">This field is required</div>
        )}
      </div>
      <div className="form-row">
        <div className="form-col">
          <label htmlFor="playerHeight">Height (cm)</label>
          <input
            type="text"
            id="playerHeight"
            name="playerHeight"
            ref={register({ required: true })}
          />
          {errors.playerHeight && (
            <div className="error">This field is required</div>
          )}
        </div>
        <div className="form-col">
          <label htmlFor="playerWeight">Weight (kg)</label>
          <input
            type="text"
            id="playerWeight"
            name="playerWeight"
            ref={register({ required: true })}
          />
          {errors.playerWeight && (
            <div className="error">This field is required</div>
          )}
        </div>
      </div>
      <div className="form-row">
        <div className="form-col">
          <label htmlFor="playerHeight">Birthday</label>
          <input
            type="date"
            id="playerBirthday"
            name="playerBirthday"
            ref={register({ required: true })}
          />
          {errors.playerBirthday && (
            <div className="error">This field is required</div>
          )}
        </div>
        <div className="form-col">
          <label htmlFor="playerNumber">Number</label>
          <input
            type="text"
            id="playerNumber"
            name="playerNumber"
            ref={register({ required: true })}
          />
          {errors.playerNumber && (
            <div className="error">This field is required</div>
          )}
        </div>
      </div>
      <input type="reset" value="Cancel" />
      <input type="submit" value="Save" className="btn" />
    </form>
  );
};

export default AddPlayer;
