import { ReactComponent as Pen } from "../../img/create.svg";
import { ReactComponent as Delete } from "../../img/delete.svg";
import {
  fetchDeleteItemById,
  SelectDeleteItemByIdResult,
} from "../../store/deleteItemById";
import { silectSideMenuId } from "../../store/sideMenuSlise";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import s from "./style.module.css";
import { useHistory } from "react-router";

interface IProps {
  itemId: number;
}

const ControlButtons: React.FC<IProps> = (props) => {
  const dispatch = useAppDispatch();
  const history = useHistory();
  const currentPageId = useAppSelector(silectSideMenuId);
  let deleteRequest = "";
  if (currentPageId === 2) {
    deleteRequest = `http://dev.trainee.dex-it.ru/api/Player/Delete?id=${props.itemId}`;
  } else if (currentPageId === 1) {
    deleteRequest = `http://dev.trainee.dex-it.ru/api/Team/Delete?id=${props.itemId}`;
  }
  const deleteRsult = useAppSelector(SelectDeleteItemByIdResult);
  console.log("deleteRequest: ", deleteRequest);
  const handleDeletePlayer = () => {
    if (window.confirm("Confirm Delete")) {
      dispatch(fetchDeleteItemById(deleteRequest));
      alert(`${deleteRsult.name} was deleted`);
      if (currentPageId === 2) {
        history.push("/players");
      } else if (currentPageId === 1) {
        history.push("/teams");
      }
    } else alert("Canceled");
  };
  console.log("deleteRsult: ", deleteRsult);

  return (
    <div className={s.buttons}>
      <button>
        <Pen />
      </button>
      <button>
        <Delete onClick={handleDeletePlayer} />
      </button>
    </div>
  );
};

export default ControlButtons;
