import React, { useState } from "react";
import { Link } from "react-router-dom";
import cn from "classnames";
import s from "./style.module.css";
import { ReactComponent as Logout } from "../../img/logout_rounded.svg";

const Sidebar: React.FC = () => {
  const [menu, setMenu] = useState(MENU);
  const handleClick = (linkId: number) => {
    setMenu(
      menu.map((item) => {
        item.id === linkId ? (item.active = true) : (item.active = false);
        return item;
      })
    );
  };
  return (
    <aside className={s.sidebar}>
      <nav className={s.sidebarNav}>
        <ul>
          {menu &&
            menu.map((item) => (
              <li key={item.id}>
                <Link
                  to={item.to}
                  className={cn(s.navLink, { [s.active]: item.active })}
                  onClick={() => {
                    handleClick(item.id);
                  }}
                >
                  <item.img />
                  <div>{item.name}</div>
                </Link>
              </li>
            ))}
        </ul>
      </nav>
      <Link className={s.logout} to="/">
        <Logout />
        <div>Sign out</div>
      </Link>
    </aside>
  );
};

export default Sidebar;
