import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";

interface IGetTeams {
  isLoading: boolean;
  data: [
    {
      name: string;
      foundationYear: number;
      division: string;
      conference: string;
      imageUrl: string;
      id: number;
    }?
  ];
  count: number;
  page: number;
  size: number;
  error: any;
}

const initialState: IGetTeams = {
  isLoading: false,
  data: [],
  count: 6,
  page: 1,
  size: 6,
  error: null,
};

export const getTeamsSlice = createSlice({
  name: "getTeams",
  initialState,
  reducers: {},
});

// export const { getTeams} = getTeamsSlice.actions
export const selectTeamsData = (state: RootState) => state.getTeams.data;
export const selectTeamsIsLoading = (state: RootState) =>
  state.getTeams.isLoading;

export default getTeamsSlice.reducer;
