import React, { useEffect } from "react";
import Select from "react-select";
import makeAnimated from "react-select/animated";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  fetchTeamsAsync,
  selectTeamsData,
  selectTeamsIsLoading,
} from "../../store/getTeamsSlice";
import { setTeamIds } from "../../store/teamsFetchSuffix";
import { ITeamSelectOptions } from "../../Interfaces";

import s from "./style.module.css";

const SelectTeams: React.FC = () => {
  const dispatch = useAppDispatch();
  useEffect(() => {
    dispatch(fetchTeamsAsync("http://dev.trainee.dex-it.ru/api/Team/GetTeams"));
  }, [dispatch]);
  const teamsRedux = useAppSelector(selectTeamsData);
  const teamsIsLoading = useAppSelector(selectTeamsIsLoading);
  const teams = teamsRedux.data;
  let teamSelectOptions: ITeamSelectOptions[] = [];
  const customTheme = (theme: any) => {
    return {
      ...theme,
      colors: {
        ...theme.colors,
        primary25: "#FF768E",
        primary: "#C60E2E",
      },
    };
  };
  const colourStyles = {
    option: (styles: any, { isDisabled, isFocused, isSelected }: any) => {
      const color = chroma(data.color);
      return {
        ...styles,
        backgroundColor: isDisabled
          ? null
          : isSelected
          ? data.color
          : isFocused
          ? color.alpha(0.1).css()
          : null,
        color: isDisabled
          ? "#ccc"
          : isSelected
          ? chroma.contrast(color, "white") > 2
            ? "white"
            : "black"
          : data.color,
        cursor: isDisabled ? "not-allowed" : "default",

        ":active": {
          ...styles[":active"],
          backgroundColor:
            !isDisabled && (isSelected ? data.color : color.alpha(0.3).css()),
        },
      };
    },
    multiValue: (styles: any) => {
      return {
        ...styles,
        backgroundColor: "#E4163A",
        borderRadius: "4px",
      };
    },
    multiValueLabel: (styles: any) => ({
      ...styles,
      color: "#fff",
      fontSize: "14px",
      lineHeight: "19px",
      paddingLeft: "4px",
    }),
    multiValueRemove: (styles: any) => ({
      ...styles,
      color: "#fff",
      ":hover": {
        backgroundColor: "#E4163A",
        color: "#fff",
        cursor: "pointer",
      },
    }),
  };

  const animatedComponents = makeAnimated();
  if (teamsIsLoading === false) {
    teamSelectOptions = teams.map((item) => ({
      value: item.id,
      label: item.name,
    }));
  }

  return (
    <div className={s.reactSelectContainer}>
      <Select
        closeMenuOnSelect={false}
        components={animatedComponents}
        isMulti
        classNamePrefix="select-teams"
        theme={customTheme}
        options={teamSelectOptions}
        styles={colourStyles}
        onChange={(data: any) => {
          const newArr = data.map((item: ITeamSelectOptions) => item.value);
          const newRequest = newArr.length
            ? "&TeamIds=" + newArr.join("&TeamIds=")
            : "";
          dispatch(setTeamIds(newRequest));
        }}
      />
    </div>
  );
};

export default SelectTeams;
