import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit'
import { RootState } from "./store"

interface IGetTeams {
    data: [
      {
        name: string,
        foundationYear: number,
        division: string,
        conference: string,
        imageUrl: string,
        id: number
      }?
    ],
    count: number,
    page: number,
    size: number
  }

const initialState: IGetTeams = {
  isLoading: false,
    data: [],
    count: 6,
    page: 1,
    size: 6
}

export const getTeamsSlice = createSlice({
    name: 'getTeamsSlice',
    initialState,
    reducers: {}
})

export const selectTeams = (state: RootState) => state.getTeams
export default getTeamsSlice.reducer
