import { SelectProps } from "../../Interfaces";
import s from "./style.module.css";

const SelectGroup: React.FC<SelectProps> = ({
  label,
  selectName,
  errorText,
  errors,
  register,
}) => {
  const playerPosition = useAppSelector(SelectPlayerPositionsData);
  const playerPositionIsLoading = useAppSelector(
    SelectPlayerPositionsIsLoading
  );
  const teamsRedux = useAppSelector(selectTeamsData);
  const teamsIsLoading = useAppSelector(selectTeamsIsLoading);
  const teams = teamsRedux.data;
  return (
    <div className={s.selectGroup}>
      <label className={s.label} htmlFor={selectName}>
        {label}
      </label>
      <select
        className={s.select}
        defaultValue=""
        name={selectName}
        ref={register({ required: true })}
      >
        <option value="" disabled>
          Select...
        </option>
        {playerPositionIsLoading
          ? "Loading"
          : playerPosition.map((item) => (
              <option key={item} value={item}>
                {item}
              </option>
            ))}
      </select>
      {errors[selectName] && <div className={s.error}>{errorText}</div>}
    </div>
  );
};

export default SelectGroup;
