import { createSlice } from "@reduxjs/toolkit";
import { RootState } from "./store";

const initialState: { id: number | string } = {
  id: "",
};

export const selectedIdSlice = createSlice({
  name: "selectedId",
  initialState,
  reducers: {
    reset: (state) => initialState,
    setId: (state, action) => (state.id = action.payload),
  },
});

export const newSelectedId = (state: RootState) => state.selectedId.id;

export const { setId } = selectedIdSlice.actions;

export default selectedIdSlice.reducer;
