import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { IFetchSuffix } from "../Interfaces";
import { RootState } from "./store";

const initialState: IFetchSuffix = {
  searchText: "",
  pageNuber: 1,
  pageSize: 6,
};

export const teamsFetchSuffixSlise = createSlice({
  name: "teamsFetchSuffix",
  initialState,
  reducers: {
    setSearchText: (state, action: PayloadAction<string>) => {
      state.searchText = action.payload;
    },
    setPageNuber: (state, action: PayloadAction<number>) => {
      state.pageNuber = action.payload;
    },
    setPageSize: (state, action: PayloadAction<number>) => {
      state.pageSize = action.payload;
    },
  },
});

export const {
  setSearchText,
  setPageNuber,
  setPageSize,
} = teamsFetchSuffixSlise.actions;
export const teamsFetchSuffix = (state: RootState) => state.teamsFetchSuffix;
export default teamsFetchSuffixSlise.reducer;
