import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";

interface IGetTeams {
  isLoading: boolean;
  data: [
    {
      name: string;
      foundationYear: number;
      division: string;
      conference: string;
      imageUrl: string;
      id: number;
    }?
  ];
  count: number;
  page: number;
  size: number;
  error: any;
}

const initialState: IGetTeams = {
  isLoading: false,
  data: [],
  count: 0,
  page: 1,
  size: 6,
  error: null,
};

export const fetchTeamsAsync = createAsyncThunk<any, IGetTeams>(
  "teams/fatchData",
  async (pageSize) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "GET",
      headers: myHeaders,
    };
    const response = await fetch(
      `http://dev.trainee.dex-it.ru/api/Team/GetTeams?PageSize=${pageSize}`,
      requestOptions
    );
    return (await response.json()) as IGetTeams;
  }
);

export const getTeamsSlice = createSlice({
  name: "getTeams",
  initialState,
  reducers: {
    reset: (state) => initialState,
  },
  extraReducers: (builder) => {
    builder.addCase(fetchTeamsAsync.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(fetchTeamsAsync.fulfilled, (state, action) => {
      state.isLoading = false;
      state.data = action.payload;
    });
    builder.addCase(fetchTeamsAsync.rejected, (state, action) => {
      state.isLoading = false;
      state.error = action.error;
    });
  },
});

export const selectTeamsData = (state: RootState) => state.getTeams.data;
export const selectTeamsIsLoading = (state: RootState) =>
  state.getTeams.isLoading;

export default getTeamsSlice.reducer;
