import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import {
  IsignUpState,
  ILoginRequest,
  ILoginResult,
} from "../Interfaces/interfaces";
import { RootState } from "./store";

const initialState: IsignUpState = {
  isLoading: true,
  fetchResult: {
    userName: "",
    avatarUrl: "",
    token: "",
  },
  errors: null,
};

export const fetchsignUp = createAsyncThunk<any, ILoginRequest>(
  "app/signUp",
  async (data) => {
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    };
    const response = await fetch(
      "http://dev.trainee.dex-it.ru/api/Auth/SignUp",
      requestOptions
    );
    const result = (await response.json()) as ILoginResult;
    localStorage.setItem("token", result.token);
    return result;
  }
);

const signUpSlice = createSlice({
  name: "signUp",
  initialState,
  reducers: {
    reset: (state) => initialState,
  },
  extraReducers: (builder) => {
    builder.addCase(fetchsignUp.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(fetchsignUp.fulfilled, (state, action) => {
      state.isLoading = false;
      state.fetchResult = action.payload;
    });
    builder.addCase(fetchsignUp.rejected, (state, action) => {
      state.isLoading = false;
      state.errors = action.error;
    });
  },
});

export const selectsignUpResult = (state: RootState) =>
  state.signUp.fetchResult;
export const selectsignUpIsLoading = (state: RootState) =>
  state.signUp.isLoading;
export const selectsignUpIsError = (state: RootState) => state.signUp.errors;

export const signUpReducer = signUpSlice.reducer;
