import { useForm } from "react-hook-form";
import { IInputGroupProps } from "../../Interfaces";

import s from "./style.module.css";

const InputGroup = ({
  label,
  type = "text",
  inputName,
  errorText,
}: IInputGroupProps) => {
  const { register, errors } = useForm();

  return (
    <div className={s.inputGroup}>
      <label className={s.label} htmlFor={inputName}>
        {label}
      </label>
      <input
        className={s.input}
        type={type}
        name={inputName}
        id={inputName}
        ref={register({ required: true })}
      />
      {errors.playerName && <div className={s.error}>{errorText}</div>}
    </div>
  );
};

export default InputGroup;
