import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  fetchTeamsAsync,
  selectTeamsData,
  selectTeamsIsLoading,
} from "../../store/getTeamsSlice";

import Select from "react-select";
import makeAnimated from "react-select/animated";

import s from "./style.module.css";

const SelectTeams: React.FC = () => {
  const dispatch = useAppDispatch();
  useEffect(() => {
    dispatch(fetchTeamsAsync("http://dev.trainee.dex-it.ru/api/Team/GetTeams"));
  }, [dispatch]);
  const teamsRedux = useAppSelector(selectTeamsData);
  const teamsIsLoading = useAppSelector(selectTeamsIsLoading);
  const teams = teamsRedux.data;
  let teamSelectOptions: any[] = [];
  const customTheme = (theme: any) => {
    return {
      ...theme,
      colors: {
        ...theme.colors,
        primary25: "#FF768E",
        primary: "#C60E2E",
      },
    };
  };
  const colourStyles = {
    control: (styles) => ({ ...styles, backgroundColor: "white" }),
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
      const color = chroma(data.color);
      return {
        ...styles,
        backgroundColor: isDisabled
          ? null
          : isSelected
          ? data.color
          : isFocused
          ? color.alpha(0.1).css()
          : null,
        color: isDisabled
          ? "#ccc"
          : isSelected
          ? chroma.contrast(color, "white") > 2
            ? "white"
            : "black"
          : data.color,
        cursor: isDisabled ? "not-allowed" : "default",

        ":active": {
          ...styles[":active"],
          backgroundColor:
            !isDisabled && (isSelected ? data.color : color.alpha(0.3).css()),
        },
      };
    },
    multiValue: (styles, { data }) => {
      const color = chroma(data.color);
      return {
        ...styles,
        backgroundColor: color.alpha(0.1).css(),
      };
    },
    multiValueLabel: (styles, { data }) => ({
      ...styles,
      color: data.color,
    }),
    multiValueRemove: (styles, { data }) => ({
      ...styles,
      color: data.color,
      ":hover": {
        backgroundColor: data.color,
        color: "white",
      },
    }),
  };

  const animatedComponents = makeAnimated();
  if (teamsIsLoading === false) {
    teamSelectOptions = teams.map((item) => ({
      value: item.id,
      label: item.name,
    }));
  }
  console.log("teamSelectOptions: ", teamSelectOptions);
  return (
    <div className={s.reactSelectContainer}>
      <Select
        closeMenuOnSelect={false}
        components={animatedComponents}
        isMulti
        classNamePrefix="select-teams"
        theme={customTheme}
        options={teamSelectOptions}
        styles={colourStyles}
      />
    </div>
  );
};

export default SelectTeams;
