import React, { useEffect } from "react";
import { useForm } from "react-hook-form";
import { IPlayerAddInputs } from "../../Interfaces";
import {
  fetchPlayerPositions,
  SelectPlayerPositionsData,
  SelectPlayerPositionsIsLoading,
} from "../../store/getPlayerPositionsSlice";
import {
  fetchTeamsAsync,
  selectTeamsData,
  selectTeamsIsLoading,
} from "../../store/getTeamsSlice";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import InputGroup from "../InputGroup";
import SelectGroup from "../SelectGroup";

const AddPlayer: React.FC = () => {
  const dispatch = useAppDispatch();

  useEffect(() => {
    dispatch(
      fetchPlayerPositions(
        "http://dev.trainee.dex-it.ru/api/Player/GetPositions"
      )
    );
    dispatch(fetchTeamsAsync("http://dev.trainee.dex-it.ru/api/Team/GetTeams"));
  }, [dispatch]);

  const playerPosition = useAppSelector(SelectPlayerPositionsData).map(
    (item, index) => {
      return {
        id: index,
        name: item,
      };
    }
  );

  const playerPositionIsLoading = useAppSelector(
    SelectPlayerPositionsIsLoading
  );
  const teamsRedux = useAppSelector(selectTeamsData);
  const teamsIsLoading = useAppSelector(selectTeamsIsLoading);
  const teams = teamsRedux.data;

  const { register, handleSubmit, errors } = useForm<IPlayerAddInputs>();
  const onSubmit = (data: {}) => console.log(data);

  return (
    <form className="form-add" onSubmit={handleSubmit(onSubmit)}>
      <div className="form-col">
        <div className="form-image">
          <InputGroup
            type="file"
            inputName="playerPhoto"
            errorText="Select player's image"
            register={register}
            required
            errors={errors}
          />
        </div>
      </div>
      <div className="form-col">
        <div className="form-info">
          <InputGroup
            label="Name"
            inputName="playerName"
            errorText="Enter player name"
            register={register}
            required
            errors={errors}
          />
          {playerPositionIsLoading ? (
            ""
          ) : (
            <SelectGroup
              label="Position"
              selectName="playerPosition"
              errorText="This field is required"
              errors={errors}
              register={register}
              options={playerPosition}
            />
          )}
          {teamsIsLoading ? (
            ""
          ) : (
            <SelectGroup
              label="Team"
              selectName="playerTeam"
              errorText="This field is required"
              errors={errors}
              register={register}
              options={teams}
            />
          )}
          <div>
            <label htmlFor="playerTeam">Team</label>
            <select
              defaultValue=""
              name="playerTeam"
              ref={register({ required: true })}
            >
              <option value="" disabled>
                Select...
              </option>
              {teamsIsLoading
                ? "Loading"
                : teams.map((item) => (
                    <option key={item.id} value={item.name}>
                      {item.name}
                    </option>
                  ))}
            </select>
            {errors.playerTeam && (
              <div className="error">This field is required</div>
            )}
          </div>
          <div className="form-row">
            <div className="form-col">
              <InputGroup
                label="Height (cm)"
                inputName="playerHeight"
                errorText="Enter player height"
                register={register}
                required
                errors={errors}
              />
            </div>
            <div className="form-col">
              <InputGroup
                label="Weight (kg)"
                inputName="playerWeight"
                errorText="Enter player weight"
                register={register}
                required
                errors={errors}
              />
            </div>
          </div>
          <div className="form-row">
            <div className="form-col">
              <InputGroup
                label="Birthday"
                type="date"
                inputName="playerBirthday"
                errorText="Enter player birthday"
                register={register}
                required
                errors={errors}
              />
            </div>
            <div className="form-col">
              <InputGroup
                label="Number"
                inputName="playerNumber"
                errorText="Enter player number"
                register={register}
                required
                errors={errors}
              />
            </div>
          </div>
          <div className="form-row">
            <div className="form-col">
              <input type="reset" value="Cancel" className="btn-text" />
            </div>
            <div className="form-col">
              <input type="submit" value="Save" className="btn" />
            </div>
          </div>
        </div>
      </div>
    </form>
  );
};

export default AddPlayer;
