import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";

interface IGetTeams {
  isLoading: boolean;
  data: [
    {
      name: string;
      foundationYear: number;
      division: string;
      conference: string;
      imageUrl: string;
      id: number;
    }?
  ];
  count: number;
  page: number;
  size: number;
  error: any;
}

const initialState: IGetTeams = {
  isLoading: false,
  data: [],
  count: 0,
  page: 1,
  size: 6,
  error: null,
};
// "http://dev.trainee.dex-it.ru/api/Team/GetTeams?PageSize=6";
const fetchTeamsAsync = createAsyncThunk(
  "teams/fatchData",
  (endpoint: any, { getState }) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "GET",
      headers: myHeaders,
    };
    return fetch(endpoint, requestOptions)
      .then((response) => {
        if (!response.ok) {
          throw Error(response.statusText);
        }
        return response.json();
      })
      .then((json) => json);
  }
);

export const getTeamsSlice = createSlice({
  name: "getTeams",
  initialState,
  reducers: {
    fetchTeams: (state) => ({
      ...state,
      isLoading: true,
    }),
    fetchTeamsResolve: (state, action: PayloadAction) => ({
      ...state,
      isLoading: false,
      data: action.payload,
    }),
    fetchTeamsReject: (state, action: PayloadAction) => ({
      ...state,
      isLoading: false,
      data: [],
      error: action.payload,
    }),
  },
});

export const {
  fetchTeams,
  fetchTeamsResolve,
  fetchTeamsReject,
} = getTeamsSlice.actions;
export const selectTeamsData = (state: RootState) => state.getTeams.data;
export const selectTeamsIsLoading = (state: RootState) =>
  state.getTeams.isLoading;

export default getTeamsSlice.reducer;
