import { IInputGroupProps } from "../../Interfaces";

import s from "./style.module.css";

type RefReturn =
  | string
  | ((instance: HTMLInputElement | null) => void)
  | React.RefObject<HTMLInputElement>
  | null
  | undefined;

type InputProps = React.DetailedHTMLProps<
  React.InputHTMLAttributes<HTMLInputElement>,
  HTMLInputElement
> & {
  label?: string;
  type?: string;
  inputName: string;
  errorText: string;
  errors: any;
  register: ({ required }: { required?: boolean }) => RefReturn;
};

const InputGroup: React.FC<InputProps> = ({
  label,
  type = "text",
  inputName,
  errorText,
  register,
  required,
  errors,
}) => {
  console.log("errors: ", errors);
  return (
    <div className={s.inputGroup}>
      {type === "file" ? (
        <label className={s.fileLabel} htmlFor={inputName}></label>
      ) : (
        <label className={s.label} htmlFor={inputName}>
          {label}
        </label>
      )}
      <input
        className={s.input}
        type={type}
        name={inputName}
        id={inputName}
        ref={register({ required })}
      />
      {errors.label && <div className={s.error}>{errorText}</div>}
    </div>
  );
};

export default InputGroup;
