import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import { ILoginRequest, ISignInInputs } from "../../Interfaces/interfaces";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { fetchSignIn, selectSignInError } from "../../store/signInSlise";
import InputGroup from "../InputGroup/iInputGroup";
import s from "./style.module.css";

const SignInForm: React.FC = () => {
  const dispatch = useAppDispatch();
  const { register, handleSubmit, errors, reset } = useForm<ISignInInputs>();
  const signInError = useAppSelector(selectSignInError);

  const onSubmit = (data: ILoginRequest) => {
    dispatch(fetchSignIn(data));
    reset();
  };
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <h1 className={s.formTitle}>Sign In</h1>
      <InputGroup
        label="Login"
        inputName="login"
        errorText="Enter login"
        register={register}
        // required
        errors={errors}
      />
      <InputGroup
        type="password"
        label="Password"
        inputName="password"
        errorText="Enter password"
        register={register}
        // required
        errors={errors}
      />
      <button className="btn">Sign In</button>
      <div className={s.signUpLnk}>
        Not a member yet? <Link to="/sign-up">Sign up</Link>
      </div>
    </form>
  );
};

export default SignInForm;
