import SignInForm from "../../Components/SignInForm/signInForm";
import signInBg from "../../img/sign-in-bg.svg";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  fetchSignIn,
  selectSignInIsLoading,
  selectSignInResult,
} from "../../store/signInSlise";

interface ISignIn {
  login: string;
  password: string;
}

const SignIn: React.FC = () => {
  const dispatch = useAppDispatch();
  const signInResponse = useAppSelector(selectSignInResult);
  const signInIsLoading = useAppSelector(selectSignInIsLoading);

  const handleSubmitLoginForm = async (data: ISignIn) => {
    dispatch(fetchSignIn(data));

    if (!signInIsLoading) {
      console.log("response: ", signInResponse);
      localStorage.setItem("token", signInResponse.token);
    }
  };
  return (
    <div className="login__wrapper">
      <div className="container">
        <div className="login__form">
          <SignInForm onSubmit={handleSubmitLoginForm} />
        </div>
        <div className="login__bg">
          <img src={signInBg} alt="" />
        </div>
      </div>
    </div>
  );
};

export default SignIn;
