import { useState } from "react";
import { InputProps } from "../../Interfaces/interfaces";

import s from "./style.module.css";

const InputGroup: React.FC<InputProps> = ({
  label,
  type = "text",
  inputName,
  errorText,
  register,
  required,
  errors,
}) => {
  const [active, setActive] = useState<boolean>(false);
  const handleClick = () => {
    setActive((prevState) => !prevState);
  };
  return (
    <div className={s.inputGroup}>
      {type === "file" ? (
        <label className={s.fileLabel} htmlFor={inputName}></label>
      ) : (
        <label className={s.label} htmlFor={inputName}>
          {label}
        </label>
      )}
      <input
        className={s.input}
        type={type}
        name={inputName}
        id={inputName}
        ref={register({ required })}
      />
      {type === "password" ? (
        <button className={s.showPassword} onClick={handleClick}></button>
      ) : (
        ""
      )}
      {errors[inputName] && <div className={s.error}>{errorText}</div>}
    </div>
  );
};

export default InputGroup;
