import { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { fetchTeamsAsync, selectTeamsData } from "../../store/getTeamsSlice";
import { PlayerDto } from "../../Interfaces";
import s from "./style.module.css";

const PlayerCard: React.FC<PlayerDto> = ({ name, number, team, avatarUrl }) => {
  const dispatch = useAppDispatch();
  const teamsRedux = useAppSelector(selectTeamsData);
  const teams = teamsRedux.data;

  const teamName = teams.filter((item) => item.id === team);
  console.log("teamName: ", teamName[0].name);

  const teamsRequest = "http://dev.trainee.dex-it.ru/api/Team/GetTeams";

  useEffect(() => {
    dispatch(fetchTeamsAsync(teamsRequest));
  }, [teamsRequest, dispatch]);

  return (
    <div className={s.player}>
      <div className={s.player__wrapper}>
        <div className={s.player__logo}>
          <img src={avatarUrl} alt={name} />
        </div>
        <div className={s.player__info}>
          <div className={s.player__name}>
            {name} {number}
          </div>
          <div className={s.player__team}>{team}</div>
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
