import { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { fetchTeamsAsync, selectTeamsData } from "../../store/getTeamsSlice";
import AddBtn from "../../Components/AddBtn";
import Search from "../../Components/Search";
import TeamCard from "../../Components/TeamCard";
import ReactPaginate from "react-paginate";
import Select from "react-select";
import { IPageSize } from "../../Interfaces";

const TeamCards: React.FC = () => {
  const dispatch = useAppDispatch();
  const teamsRedux = useAppSelector(selectTeamsData);

  const teams = teamsRedux.data;
  const currentPage = 1;
  const pageSize = 6;
  const loadedTeamsNumber = teamsRedux.count;
  const pageCount = Math.ceil(loadedTeamsNumber / pageSize);
  const handlePageClick = (selectedItem: { selected: number }) => {
    const currentPage = selectedItem.selected + 1;
    const request = `http://dev.trainee.dex-it.ru/api/Team/GetTeams?Name=${""}&Page=${currentPage}&PageSize=${pageSize}`;
    dispatch(fetchTeamsAsync(request));
  };

  const pageSizeOptions: IPageSize[] = [
    { value: "6", label: "6" },
    { value: "12", label: "12" },
    { value: "24", label: "24" },
  ];

  const handleChangePageSize = (data: any) => {
    const pageSize = data.value;
    const request = `http://dev.trainee.dex-it.ru/api/Team/GetTeams?Name=${""}&Page=${currentPage}&PageSize=${pageSize}`;
    dispatch(fetchTeamsAsync(request));
  };
  const customTheme = (theme: any) => ({
    ...theme,
    colors: {
      ...theme.colors,
      primary25: "#FF768E",
      primary: "black",
    },
  });

  const request = `http://dev.trainee.dex-it.ru/api/Team/GetTeams?Name=${""}&Page=${currentPage}&PageSize=${pageSize}`;
  useEffect(() => {
    dispatch(fetchTeamsAsync(request));
  }, [request]);

  return (
    <>
      <div className="page-content__top">
        <Search />
        <AddBtn />
      </div>
      <div className="cards-wrapper">
        {teams.map(({ name, foundationYear, imageUrl, id }: any) => {
          return (
            <TeamCard
              key={id}
              id={id}
              name={name}
              imageUrl={imageUrl}
              foundationYear={foundationYear}
            />
          );
        })}
      </div>
      <div className="page-content__bottom">
        <ReactPaginate
          previousLabel={"<"}
          nextLabel={">"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={pageCount}
          marginPagesDisplayed={1}
          pageRangeDisplayed={3}
          onPageChange={handlePageClick}
          containerClassName={"pagination"}
          // subContainerClassName={"pages pagination"}
          activeClassName={"active"}
        />
        <Select
          className="page-size"
          theme={customTheme}
          options={pageSizeOptions}
          defaultValue={pageSizeOptions[0]}
          onChange={handleChangePageSize}
        />
      </div>
    </>
  );
};

export default TeamCards;
