import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import { ISignUpInputs, ISignUpRequest } from "../../Interfaces/interfaces";
import { useAppDispatch } from "../../store/hooks";
import { fetchsignUp } from "../../store/signUpSlice";
import Checkbox from "../Checkbox/checkbox";
import InputGroup from "../InputGroup/iInputGroup";
import s from "./style.module.css";

const SignUpForm: React.FC = () => {
  const dispatch = useAppDispatch();
  const { register, handleSubmit, errors, reset } = useForm<ISignUpInputs>();
  const onSubmit = (data: ISignUpRequest) => {
    dispatch(fetchsignUp(data));
    reset();
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <h1 className={s.formTitle}>Sign Up</h1>
      <InputGroup
        label="Login"
        inputName="login"
        errorText="Enter login"
        register={register}
        // required
        errors={errors}
      />
      <Checkbox name="agreement" label="I accept the agreement" />
      <button className="btn">Sign Up</button>
      <div className={s.signUpLnk}>
        Already a member? <Link to="/sign-in">Sign in</Link>
      </div>
    </form>
  );
};

export default SignUpForm;
