import { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  fetchPlayersAsync,
  selectPlayersData,
} from "../../store/getPlayersSlice";
import { selectTeamsFetchSuffix } from "../../store/teamsFetchSuffix";

import AddBtn from "../../Components/AddBtn";
import Search from "../../Components/Search";
import PlayerCard from "../../Components/PlayerCard";
import SelectPageSize from "../../Components/SelectPageSize";
import Pagination from "../../Components/Pagination";
import EmptyImg from "../../img/empty-player.svg";
import EmptyBase from "../../Components/EmptyBase";
import { IFetchSuffix } from "../../Interfaces";
import SelectTeams from "../../Components/SelectTeams";
import selectedIdSlise, { setId } from "../../store/selectedIdSlise";

const PlayersCards: React.FC = () => {
  const dispatch = useAppDispatch();
  const playersRedux = useAppSelector(selectPlayersData);
  const {
    searchText,
    pageNumber,
    pageSize,
    teamIds,
  }: IFetchSuffix = useAppSelector(selectTeamsFetchSuffix);

  const players = playersRedux.data;

  const request = `http://dev.trainee.dex-it.ru/api/Player/GetPlayers?Name=${searchText}${teamIds}&Page=${pageNumber}&PageSize=${pageSize}`;

  useEffect(() => {
    dispatch(fetchPlayersAsync(request));
  }, [request, dispatch]);
  const loadedCardsNumber = playersRedux.count;
  const handleClick = (id: number) => {
    dispatch(setId(id));
  };
  return (
    <>
      <div className="page-content__top">
        <Search />
        <SelectTeams />
        <AddBtn />
      </div>
      {players.length ? "" : <EmptyBase imageUrl={EmptyImg} />}
      {players.length && (
        <>
          <div className="cards-wrapper">
            {players.map(({ name, avatarUrl, id, number, team }: any) => {
              return (
                <PlayerCard
                  key={id}
                  id={id}
                  name={name}
                  number={number}
                  team={team}
                  avatarUrl={avatarUrl}
                  onClick={handleClick}
                />
              );
            })}
          </div>
          <div className="page-content__bottom">
            <Pagination loadedCardsNumber={loadedCardsNumber} />
            <SelectPageSize />
          </div>
        </>
      )}
    </>
  );
};

export default PlayersCards;
