import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "./store";

interface IGetTeams {
  isLoading: string;
  data: [
    {
      name: string;
      foundationYear: number;
      division: string;
      conference: string;
      imageUrl: string;
      id: number;
    }?
  ];
  count: number;
  page: number;
  size: number;
  error: any;
}

const initialState: IGetTeams = {
  isLoading: "yes",
  data: [],
  count: 0,
  page: 1,
  size: 6,
  error: null,
};

const fetchTeamsAsync = createAsyncThunk(
  "teams/fatchData",
  async (pageSige: number) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "GET",
      headers: myHeaders,
    };
    const response = await fetch(
      `http://dev.trainee.dex-it.ru/api/Team/GetTeams?PageSize=${pageSige}`,
      requestOptions
    );
    return (await response.json()) as IGetTeams;
  }
);

export const getTeamsSlice = createSlice({
  name: "getTeams",
  initialState,
  reducers: {
    reset: (state) => initialState,
  },
  extraReducers: {
    [fetchTeamsAsync.pending]: (state) => ({
      ...state,
      isLoading: "yes",
    }),
    [fetchTeamsAsync.fulfilled]: (state, action: PayloadAction) => ({
      ...state,
      isLoading: "",
      data: action.payload,
    }),
    [fetchTeamsAsync.rejected]: (state, action: PayloadAction) => ({
      ...state,
      isLoading: "",
      data: [],
      error: action.payload,
    }),
  },
});

export const {
  fetchTeams,
  fetchTeamsResolve,
  fetchTeamsReject,
} = getTeamsSlice.actions;
export const selectTeamsData = (state: RootState) => state.getTeams.data;
export const selectTeamsIsLoading = (state: RootState) =>
  state.getTeams.isLoading;

export default getTeamsSlice.reducer;
