import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { PlayerPositionsState } from "../Interfaces";
import { RootState } from "./store";

const initialState: PlayerPositionsState = {
  isLoading: false,
  fetchResult: [],
  error: null,
};

export const fetchPlayerPositions = createAsyncThunk<any, string>(
  "player/fetchData",
  async (request) => {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    });
    const requestOptions = {
      method: "GET",
      headers: myHeaders,
    };
    const response = await fetch(request, requestOptions);
    return (await response.json()) as string[];
  }
);

export const getPlayerPositionsSlice = createSlice({
  name: "getPlayerPositions",
  initialState,
  reducers: {
    reset: (state) => initialState,
  },
  extraReducers: (builder) => {
    builder.addCase(fetchPlayerPositions.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(fetchPlayerPositions.fulfilled, (state, action) => {
      state.isLoading = false;
      state.fetchResult = action.payload;
    });
    builder.addCase(fetchPlayerPositions.rejected, (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    });
  },
});

export const SelectSinglePlayerData = (state: RootState) =>
  state.getPlayerPositions.fetchResult;
export const SelectSinglePlayerIsLoading = (state: RootState) =>
  state.getPlayerPositions.isLoading;
export const SelectSinglePlayerError = (state: RootState) =>
  state.getPlayerPositions.error;

export default getPlayerPositionsSlice.reducer;
