import { createSlice } from "@reduxjs/toolkit";
import { IMenu } from "../Interfaces";
import { ReactComponent as TeamsImg } from "../img/group_person_rounded.svg";
import { ReactComponent as PlayersImg } from "../img/person_rounded.svg";
import { RootState } from "./store";

const initialState: IMenu[] = [
  {
    id: 1,
    name: "Teams",
    img: TeamsImg,
    active: false,
    to: "/team-cards",
  },
  {
    id: 2,
    name: "Players",
    img: PlayersImg,
    active: false,
    to: "/player-cards",
  },
];

export const sideMenuSlise = createSlice({
  name: "sideMenu",
  initialState,
  reducers: {
    reset: (state) => initialState,
    setMenu: (state, action) => {
      state.map((item) => {
        item.id === action.payload
          ? (item.active = true)
          : (item.active = false);
        return item;
      });
    },
  },
});

export const { setMenu } = sideMenuSlise.actions;
export const silectSideMenu = (state: RootState) => state.sideMenu;
export default sideMenuSlise.reducer;
