import React from "react";
import ReactPaginate from "react-paginate";
import { selectTeamsData } from "../../store/getTeamsSlice";
import {
  setPageNumber,
  selectTeamsFetchSuffix,
} from "../../store/teamsFetchSuffix";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { IFetchSuffix } from "../../Interfaces";
import s from "./style.module.css";

const Pagination: React.FC = () => {
  const dispatch = useAppDispatch();
  const teamsRedux = useAppSelector(selectTeamsData);
  const { pageSize, pageNumber }: IFetchSuffix = useAppSelector(
    selectTeamsFetchSuffix
  );
  const loadedTeamsNumber = teamsRedux.count;
  const pageCount = Math.ceil(loadedTeamsNumber / pageSize);
  return (
    <ReactPaginate
      previousLabel={"<"}
      nextLabel={">"}
      breakLabel={"..."}
      breakClassName={"break-me"}
      pageCount={pageCount}
      marginPagesDisplayed={1}
      pageRangeDisplayed={3}
      onPageChange={(selectedItem: { selected: number }) =>
        dispatch(setPageNumber(selectedItem.selected + 1))
      }
      containerClassName={s.pagination}
      // subContainerClassName={"pages pagination"}
      activeClassName={s.active}
      initialPage={pageNumber}
    />
  );
};

export default Pagination;
