import React, { useEffect } from "react";
import { useForm } from "react-hook-form";
import {
  fetchPlayerPositions,
  SelectPlayerPositionsData,
} from "../../store/getPlayerPositionsSlice";
import { useAppDispatch, useAppSelector } from "../../store/hooks";

type Inputs = {
  playerName: string;
  playerPosition: string;
  playerTeam: string;
  playerHeight: string;
  playerWeight: string;
  playerBirthday: string;
  playerNumber: string;
};

const AddPlayer: React.FC = () => {
  const dispatch = useAppDispatch();
  const playerPosition = useAppSelector(SelectPlayerPositionsData);
  console.log("playerPosition: ", playerPosition);

  const request = "http://dev.trainee.dex-it.ru/api/Player/GetPositions";

  useEffect(() => {
    dispatch(fetchPlayerPositions(request));
  }, [request, dispatch]);

  const { register, handleSubmit, errors } = useForm<Inputs>();
  const onSubmit = (data: {}) => console.log(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="form-row">
        <label htmlFor="playerName">Name</label>
        <input
          type="text"
          name="playerName"
          id="playerName"
          ref={register({ required: true })}
        />
        {errors.playerName && (
          <div className="error">This field is required</div>
        )}
      </div>
      <div className="form-row">
        <label htmlFor="playerPosition">Position</label>
        <select name="playerPosition" ref={register({ required: true })}>
          {playerPosition &&
            playerPosition.map((item) => <option value={item}>{item}</option>)}
        </select>
        {errors.playerName && (
          <div className="error">This field is required</div>
        )}
      </div>

      <div className="form-row">
        <div className="form-col">
          <label htmlFor="playerHeight">Height (cm)</label>
          <input
            type="text"
            id="playerHeight"
            name="playerHeight"
            ref={register({ required: true })}
          />
          {errors.playerHeight && (
            <div className="error">This field is required</div>
          )}
        </div>
        <div className="form-col">
          <label htmlFor="playerWeight">Weight (kg)</label>
          <input
            type="text"
            id="playerWeight"
            name="playerWeight"
            ref={register({ required: true })}
          />
          {errors.playerWeight && (
            <div className="error">This field is required</div>
          )}
        </div>
      </div>
      <div className="form-row">
        <div className="form-col">
          <label htmlFor="playerHeight">Birthday</label>
          <input
            type="text"
            id="playerBirthday"
            name="playerBirthday"
            ref={register({ required: true })}
          />
          {errors.playerBirthday && (
            <div className="error">This field is required</div>
          )}
        </div>
        <div className="form-col">
          <label htmlFor="playerNumber">Number</label>
          <input
            type="text"
            id="playerNumber"
            name="playerNumber"
            ref={register({ required: true })}
          />
          {errors.playerNumber && (
            <div className="error">This field is required</div>
          )}
        </div>
      </div>

      <input type="reset" value="Cancel" />
      <input type="submit" value="Save" className="btn" />
    </form>
  );
};

export default AddPlayer;
